package DynamicPrograming;

import java.util.*;

public class PalindromePartation {

	//static String s="";
	static String s="";
	static int t[][];
	public static void main(String args[]) {
		s="";
		t=new int[s.length()][s.length()];
		for(int[] arr:t) {
			Arrays.fill(arr,-1);
		}
		//System.out.println(solve(s,0,s.length()-1));
		System.out.println(solveMemo(0,s.length()-1));
	}
	
	
	// Recursive Soln
	public static int solve(String s,int i,int j) {
		if(i>=j) {
			return 0;
		}
		
		if(isPalin(i,j)==true) {
			return 0;
		}
		
		int mn=Integer.MAX_VALUE;
		for(int k=i;k<j;k++) {
			int temp=1+solve(s,i,k)+solve(s,k+1,j);
			if(temp<mn) {
				mn=temp;
			}
		}
		return mn;
	}
	
	//Memoised Solution
	public static int solveMemo(int i,int j) {
		if(i>=j) {
			return 0;
		}
		
		if(isPalin(i,j)==true) {
			return 0;
		}
		
		if(t[i][j]!=-1) {
			return t[i][j];
		}
		
		int mn=Integer.MAX_VALUE;
		for(int k=i;k<j;k++) {
			int temp1=t[i][k]!=-1?t[i][k]:solveMemo(i,k);
			t[i][k]=temp1;
			int temp2=t[k+1][j]!=-1?t[k+1][j]:solveMemo(k+1,j);
			t[k+1][j]=temp2;
			int temp=1+temp1+temp2;
			if(temp<mn) {
				mn=temp;
			}
		}
		t[i][j]=mn;
		return t[i][j];
	}
	
	public static boolean isPalin(int i,int j) {
		if(i==j) {
			return true;
		}
		
		while(i<j) {
			if(s.charAt(i)!=s.charAt(j)) {
				return false;
			}
			i++;
			j--;
		}
		return true;		
	}
}
